import React, { Component } from "react";
import { getById } from "../api/products";
import {addToCart} from "../Actions/actions";
import {connect} from "react-redux";

class OnlineProduct extends Component{


    state ={
        loading: true,
        quantity : 1,
        foood: {}
    };

    componentDidMount(){
        const id = this.props.match.params.id;

        getById(parseInt(id)) 
           .then(foood => {
               this.setState({
                   foood,
                   loading: false
               });
           })

    }

    handleQuantity = (event) => {
        const value = event.target.value;
        if(value < 1)
        return ;

        this.setState({
            quantity: value
        })
    }
    addToCart = (foood) => {
        this.props.addToCart(foood, this.state.quantity);
    }


    render(){
        if(this.state.loading)
        return 'Loading ^..^';
        const foood = this.state.foood;
        const quantity =this.state.quantity;

        return (
            <div>
                <div className={'row'}>

                    <div className="col-6">
                    <h1>{foood.name}</h1>
                    <p className="card-text">{foood.channel1}</p>
                    <p className="card-text">{foood.channel2}</p>
                    <p className="card-text">{foood.channel3}</p>
                    <p className="card-text">{foood.channel4}</p>
                    <p className="card-text">{foood.channel5}</p>
                    <p className="card-text">{foood.channel6}</p>
                    </div>

                </div>
                </div>


        );
    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        addToCart: (productInfo, quantity) => dispatch(addToCart(productInfo, quantity)),
    };
}

export default connect(null, mapDispatchToProps)(OnlineProduct);
